import React, { useState } from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const App  = () =>{
       
     const [num ,setnum] = useState(0);
       
     const add = () =>{
             setnum(num+1);
     }
          
     return (
          <>
          <button  className='btn btn-success' onClick={add}> Click Me {num} </button>

          </>
     )
}

export default App;
