import React, { useState } from 'react';
import './index.css'
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
const App = ()=>{
  
     const [item,setItem] = useState("Mangeoes");

     const itemEvent = ()=>{
          setItem(event.target.value);
     }
     return (
          <div className='main_div' >
               <div className='center_div' >
                <br />
                  <h1>ToDoList</h1>
                <br />
                <input type='text' placeholder='Add an items' onChange={itemEvent}></input>
                <Button  variant="outlined" color="primary" className='btn' ><AddIcon /></Button> 
               <br />
               <ol>
                    <li>{item}</li>
               </ol>
               <br />
               </div>
          </div>
     )
}
export default App;
