import React ,{useState} from 'react';

const Myaccordian = ({question,answer})=>{
       
     const [item,setitem] = useState(false);
      
   const open = ()=>{
         setitem(true);
   }
     return (
          <>
               <h1 onClick={()=>setitem()}>{question}</h1>
                 {item && <p>{answer}</p>}
          </>
     )
}
export default Myaccordian;
