import React, {useState} from 'react';
import './index.css'
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';
import ListComp from './ListComp';
const App = () =>{
       
     const [item,setitem] = useState("");
     const [allitem,setallitem] = useState([]);

     const listitems =(event)=>{
          setitem(event.target.value);
     }
    const additems  = () =>{
           setallitem((prevItems)=>{
               return [...prevItems,item];
           })
           setitem("");
    }

       return (
          <>
               <div className='main_div'>
                    <div className='center_div'>
                       <h1>ToDoList</h1>
                       <input type='text' value={item} placeholder='Enter your Items' onChange={listitems} />
                       <Button variant="outlined" color="primary" onClick={additems}>
                            <AddIcon />  
                       </Button>  
                     <br/>
                    <ol>
                   { allitem.map((val,index)=>{
                        return <ListComp text={val} key={index}  />
                    })}
                    </ol>
                     <br/>


                      </div>
                    </div>
          </>
       )
}

export default App;
