import React, {} from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const App = () => {

     const getCovidData = async () => {
          const data = await fetch('https://api.covid19india.org/data.json').json();
          console.log(data.statewise);
     }

     useEffect(() => {
          getCovidData();
     });

     return (
          <>
               <h1 className='text-center text-success text-capitilize mt-5' >INDIA COVID-19 Dashboard</h1>
          </>
     )
}

export default App;
