import React, { createContext } from 'react';
import Comp from './Componet';

const FirstName = createContext();
const LastName = createContext();

const App = () =>{

      return (

          <>
              <FirstName.Provider value={"Ashvani Singh Gurjar"}>
              <LastName>
              <Comp/>
              </LastName>

              </FirstName.Provider>
          </>
      )
}

export default App;
export {FirstName,LastName}
