import React ,{useState} from 'react';

const Myaccordian = ({question,answer})=>{
       
     const [item,setitem] = useState("");
      
   const open = ()=>{
         setitem({answer})
   }
     return (
          <>
               <h1 onclick={open}>{question}</h1>
               <h1 >{answer}</h1>
          </>
     )
}
export default Myaccordian;
