import React, { useEffect, useState } from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const App = ()=>{
      
     const [num,setnum] = useState();

     useEffect(()=>{
          async function getData(){
               const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
               console.log(res.data);
          }
          getData(); 
     })

     return (
          <>
          <div className='container text-center'>
            <h1 className='text-center text-captilize'>The name of <span className='text-danger'>{num}</span> country</h1>
            <h1 className='text-center text-captilize'>The name of {num} country</h1>
            <select value={num} onChange={(event)=>{setnum(event.target.value)}} >
               <option value='1'>1</option>
               <option value='25'>25</option>
               <option value='3'>3</option>
               <option value='4'>4</option>
               <option value='5'>5</option>
            </select>
          </div>
          </>
     )
}
export default App;
