          setItem(event.target.value);
     }



     return (
          <div className='main_div' >
               <div className='center_div' >
                <br />
                  <h1>ToDoList</h1>
                <br />
                <input type='text' placeholder='Add an items' onChange={itemEvent}></input>
                <Button  variant="outlined" color="primary" className='btn' ><AddIcon /></Button> 
               <br />
               <ol>
                    
               </ol>
               <br />
               </div>
          </div>
     )
}
export default App;
