import React, {useEffect, useState} from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const App = () => {

       let final;
     const getCovidData = async () => {
          const data = await fetch('https://data.covid19india.org/data.json');
          const response = await data.json();
            final = response.statewise;
     }
     const [data , setdata] = useState(final);


     useEffect(() => {
          getCovidData();
     },[]);

     return (
          <>
               <h1 className='text-center text-success text-capitilize mt-5' >INDIA COVID-19 Dashboard</h1>
               <h2 className='container-fl'>
                   { data.map((val)=>{
                         return val;
                    })}
               </h2>
          </>
     )
}

export default App;
