import React, { useEffect, useState } from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const App  = () =>{
       
     const [num ,setnum] = useState(0);
     
     // this is always run when click
     // useEffect(()=>{
     //      alert(`${num} Clicked1`)
     // })

     const add = ()=>{
          setnum(num+1);
          
          useEffect(()=>{
               alert(`${num} Clicked1`);
              
          })
     }

     // this is block the value increaseing 
     // const add = (()=>{
     //      setnum(num+1);
     // },()=>{
     //      alert(`${num} Clicked2`)
     // })
          
     return (
          <>
          <h1 className='text-center text-capitilize mt-5' >This is a button</h1>
          <button className='btn btn-success my-5 mx-5' onClick={add}> Click Me 1 {num} </button>
          <button className='btn btn-success my-5 mx-5' onClick={add}> Click Me 2 {num} </button>

          </>
     )
}

export default App;
